<?phpdefine("THEME_DIR", get_template_directory_uri());if (!isset($content_width)) $content_width = 770;// Teme Setupfunction kleinadr_setup() {	require 'inc/general/class-Upbootwp_Walker_Nav_Menu.php';	add_theme_support( 'automatic-feed-links' );	add_theme_support( 'post-formats', array( 'aside', 'image', 'video', 'quote', 'link' ) );	add_theme_support( 'title-tag' );	add_theme_support( 'post-thumbnails' ); 	add_theme_support( 'html5', array( 'comment-list', 'comment-form', 'search-form', 'gallery', 'caption' ) );	// add_image_size( 'gallery-lg', 600, 400, true );	add_filter('widget_text', 'do_shortcode');	}add_action( 'after_setup_theme', 'kleinadr_setup' );// Them Sidebars and Widget Areasfunction kleinadr_widgets_init() {	register_sidebar(array(		'name'          => 'Sidebar',		'id'            => 'sidebar-1',		'before_widget' => '<aside id="%1$s" class="widget %2$s">',		'after_widget'  => '</aside>',		'before_title'  => '<h1 class="widget-title">',		'after_title'   => '</h1>',	));	register_sidebar(array(		'name'          => 'Slideshow',		'id'            => 'slideshow',		'before_widget' => '<aside id="%1$s" class="widget slideshow-widget %2$s">',		'after_widget'  => '</aside>',		'before_title'  => '',		'after_title'   => '',	));}add_action( 'widgets_init', 'kleinadr_widgets_init' );// Theme CSS & Scriptsfunction kleinadr_theme_scripts() {	wp_register_script( 'kleinadr-basefile', THEME_DIR . '/js/bootstrap.min.js', '','',true);	wp_register_script( 'kleinadr-stickup', THEME_DIR . '/js/stickUp.min.js"','','',false);	wp_register_style( 'kleinadr-css', THEME_DIR . '/css/upbootwp.min.css', '','','all');	wp_register_style( 'stylesheet-css', THEME_DIR . '/style.css', '','','all');	wp_enqueue_script(array('jquery','kleinadr-basefile','kleinadr-stickup'));	wp_enqueue_style(array('kleinadr-css','stylesheet-css'));}add_action( 'wp_enqueue_scripts', 'kleinadr_theme_scripts' );// Theme Menusfunction kleinadr_menus() {  register_nav_menus(    array(      'primary' => 'Primary Menu',      'footer-menu' => 'Footer Menu'    )  );}add_action( 'init', 'kleinadr_menus' );